class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        stack<int>s;
        int cnt=0;
        for(int i=0;i<c.size()+k-1;i++){
            int u=(i%(c.size()));
            if(s.size()==0){
                s.push(c[u]);
            }
            else{
                if(s.top()==c[u]){
                    while(s.size()>0){
                        s.pop();
                    }
                }
                s.push(c[u]);
            }
            if(s.size()>=k){
                cnt++;
            }
        }
        return cnt;

    }
};
