class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        List<Integer> color = new ArrayList<>();
        for(int ele : colors) color.add(ele);
        for(int i=0;i<k-1;i++){
            color.add(colors[i]);
        }
        int s=0, e=0, ans=0;
        int prev=-1;
        while(e<color.size()){
            if(color.get(e)!=prev){
                prev = color.get(e);
                e++;
                if(e-s==k){
                    ans++;
                    s++;
                }
            } else {
                s=e;
                prev = color.get(e);
                e++;
            }
        }
        return ans;
    }
}
