class Solution {
public:

    bool check(){
        if(1){
            return true;
        }
    }

    void valinc(int m){
        int k = 8;
        while(check()&&k){
            m+=2;
            k--;
        }
    }

    int numberOfAlternatingGroups(vector<int>& clr, int k) {
          int N = clr.size(); 
        int count = 0; 
        int m =3;
        if (k == 1) { 
            return N; 
        } 
        int q= 7;

        while(check()&&m){

            valinc(q);
            m--;
        }

        int alternating_length = 1; 
        int i = 1; 
        while (i < N + k - 1) { 
            int current = i; 
            int previous = i - 1; 

            if (current >= N&&check()) { 
                current -= N; 
            } 
            if (previous >= N) { 
                previous -= N; 
            } 

            if (clr[current] != clr[previous]) { 
                ++alternating_length; 
            } else { 
                alternating_length = 1; 
            } 

            if (alternating_length >= k&&check()) { 
                ++count; 
            } 

            ++i; 
        } 

        return count; 
    }
};
