class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        deque<int>q;

        int i =0;
        int j =0;
        int cnt =0;
        int n= colors.size();
        for(int i =0;i<min(n,k-1);i++){
            colors.push_back(colors[i]);
        }
        n= colors.size();
        while(j<n)
        {
            if(q.empty()){
                q.push_back(colors[j]);
                j++;    
            }
            else{

                if(q.back()==colors[j])
                {
                    q.clear();
                    q.push_back(colors[j]);
                }
                else{
                    q.push_back(colors[j]);

                    if(q.size()>=k){
                        cnt++;
                    }
                }

                j++;

            }



        }
        return cnt;


    }
};
