class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v=colors;
        for(int i=0;i<k-1;i++)
        {
            v.push_back(colors[i]);
        }
        // for(auto x:v)
        // {
        //     cout<<x<<' ';
        // }
        cout<<endl;
        int n=v.size();
        int curcount=1;
        int ans=0;
        for(int i=0;i<v.size()-1;i++)
        {
            if(v[i]!=v[i+1])
            {
                curcount++;
                if(curcount==k)
                {
                    ans++;
                    curcount--;
                }
            }
            else
            {
                curcount=1;
            }
            // cout<<curcount <<"    "<<i<<endl;
        }
        return ans;

    }
};
