class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
         int cnt=0,n=arr.size();
         if(k==1){
            return n;
         }
         int tot=1;
        for(int i=1;i<n+k-1;i++){
            int curr=i,prev=i-1;
            if(curr>=n){
                curr-=n;
            }
            if(prev>=n){
                prev-=n;
            }
            if(arr[curr]!=arr[prev]){
                tot++;
            }
            else{
                tot=1;
            }
            if(tot>=k){
                cnt++;
            }

        }
        return cnt;
    }
};
