class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        Stack<Integer> stack=new Stack<>();
        stack.push(0);
        int maxlen[]=new int[colors.length];
        int a[]=new int[colors.length*2];
        for(int j=0;j<colors.length;j++)
        {
            a[j]=colors[j];
            a[j+colors.length]=colors[j];
        }
        for(int j=1;j<a.length;j++)
        {
            if(a[j]==a[j-1])
            {
                while(!stack.isEmpty())
                {
                    if(stack.peek()<colors.length)
                    maxlen[stack.peek()]=j-1;
                    stack.pop();
                }
            }
            stack.push(j);
        }
        while(!stack.isEmpty())
        {
            if(stack.peek()<colors.length)
            maxlen[stack.peek()]=stack.peek()+k;
            stack.pop();
        }
        int ans=0;
        for(int j=0;j<colors.length;j++)
        {
            if(maxlen[j]-j+1>=k)
                ans++;
        }
        return ans;
    }
}
