class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        int akash = 0;
        int i = 0, j = 1;

        while(i < n && j < n + k){
            int st = j;
            int ed = st % n;

            while(colors[ed] != colors[(ed + n - 1) % n] && st < (n + k - 1)){
                st++;
                ed = st % n;
            }

            akash += max((st - (i + k - 1)), 0);
            i = st;
            j = i + 1;
        }
        return akash;
    }
};
