class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length; 
        int count =0; 
        int i =0; 
        int j=1;
        while(j<n+k&&i<n){
            int t = j;
            int t1 = t %n;
            while(colors[t1]!=colors[(t1+n-1)%n] && (t < (n+k-1))){
                t++;
                t1 = t%n;
            }
            count+=Math.max(t-i-k+1,0);
            i = t;
            j=i+1;
        }
        return count;
    }
}
