class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int counter = 0;
        int n = colors.length;
        int to = 1;
        for (int from = 0; from < n; from++) {
            to = Math.max(to, from + 1);
            while (to < from + k && colors[(to - 1) % n] != colors[to % n]) {
                to++;
            }
            if (to == from + k) {
                counter++;
            }
        }
        return counter;
    }
}
