class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int one=0,zer=0;
        for(auto &i:colors)
        {
            one+=i==1;
            zer+=i==0;
        }
        if(one==0||zer==0) return 0;
        for(int i=0;i<k;i++) colors.push_back(colors[i]);
        int n=colors.size(),ans=0;
        vector<int>alter(n+1);
        for(int i=1;i<n;i++) alter[i]=alter[i-1]+(colors[i]!=colors[i-1]);
        for(int i=k;i<n;i++) ans+=alter[i]-alter[i-k+1]==k-1;
        return ans;
    }
};
