class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
    if (k > n) return 0;

    vector<bool> gapp(n);
    for (int i = 0; i < n; ++i) {
        gapp[i] = (v[i] != v[(i + 1) % n]);
    }

    int gappc = 0;
    for (int i = 0; i < k-1; ++i) {
        if (gapp[i]) gappc++;
    }
    int c = (gappc == k-1) ? 1 : 0;

    for (int i = 1; i < n; ++i) {
        if (gapp[i - 1]) gappc--;

        if (gapp[(i + k - 2) % n]) gappc++;

        if (gappc == k-1) c++;
    }

    return c;

    }
};
