class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> arr(n+n,0);
        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                arr[i]++;
            }
        }
        for(int i=n;i<2*n;i++){
            if(colors[i%n]!=colors[(i-1)%n]){
                arr[i]++;
            }
        }
        for(int i=1;i<2*n;i++){
            if(arr[i]==1){
                arr[i]+=arr[i-1];

            }
        }
        int count=0;
        for(int i=0;i<n+k-1;i++){
            if(arr[i]>=k-1) count++;
        }
        return count;
    }
};
