class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int i=0;
        int j=0;
        int ans = 0;
        while(j<(n+k-2)){
            ++j;
            // cout << j << " " << i << endl;
            if(colors[j%n]==colors[(j-1)%n]){
                i = j;
            }
            if((j-i)==(k-1)){
                // cout << "inif" << endl;
                // cout << j << " " << i << endl;
                ++ans;
                ++i;
            }
        }
        return ans;
    }
};
