class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        map<int,int>mp;
        int n=colors.size();
        for(int i=1;i<n;i++){
            if(colors[i]==colors[i-1])
            mp[i]++;
        }
        if(colors[n-1]==colors[0])
        mp[0]++;
        int j = 0;
        int ans = 0;
        int cnt = 0;
        for(int i=0;j<n;i++){
            int ind=i%n;
            if(mp.count(ind))
            cnt++;
            if(i-j+1==k){
                if(mp.count(j))
                cnt--;
                if(cnt==0)
                ans++;
                j++;
            }
        }
        return ans;
    }
};
