class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {
        int numFound = 0;
        int i = 0;
        int j = 1;
        int len = arr.length;

        while (j < len + k && i < len) {
            int tempJ = j;
            int tempEnd = tempJ % len;

            while (arr[tempEnd] != arr[(tempEnd - 1 + len) % len] && tempJ < (len + k - 1)) {
                tempJ++;
                tempEnd = tempJ % len;
            }

            numFound += Math.max(tempJ - i - k + 1, 0);
            i = tempJ;
            j = i + 1;
        }

        return numFound;
    }
}
