class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v = colors;
        int n = (int)v.size();
        int len = 1;
        int ans = 0;
        for(int i = 1; i < n + k - 1; i ++){
            if(v[i % n] != v[(i - 1) % n]) len ++;
            else len = 1;
            if(len >= k){
                ans ++;
            }
        }
        return ans;
    }
};
