class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {

        int n = v.size();

        int cnt = 0;

        int ans = 0;

        for(int i = 0; i + 1 < k; i++)
        {
            if(v[i] != v[i+1])
            {
                cnt++;
            }
        }

        int pref[n];

        int suff[n];

        pref[0] = 0;

        for(int i = 1; i + 1 < n; i++)
        {
            if(v[i] != v[i-1])
            {
                pref[i] = pref[i-1] + 1;
            }
            else
            {
                pref[i] = pref[i-1];
            }
        }

        suff[n-1] = 0;

        for(int i = n-2; i >= 0; i--)
        {
            if(v[i] != v[i+1])
            {
                suff[i] = suff[i+1] + 1;
            }
            else
            {
                suff[i] = suff[i+1];
            }
        }

        for(int i = n - k + 1; i < n; i++)
        {
            int cn = suff[i] + pref[k-(n-i) - 1];

            if(v[n-1] != v[0])
            {
                cn++;
            }

            if(cn == k-1)
            {
                ans++;
            }
        }

        if(cnt == k-1)
        {
            ans++;
        }

        for(int i = 1; i <= n-k ; i++)
        {
            int j = i + k - 1;

            if(v[i] != v[i-1])
            {
                cnt--;
            }

            if(v[j] != v[j-1])
            {
                cnt++;
            }

            if(cnt == k-1)
            {
                ans++;
            }
        }

        return ans;


    }
};
