class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        l = 0
        q = deque()
        for i in range(k-1):
            if q and q[-1] == colors[i]:
                q = deque()
            q.append(colors[i])
        ans = 0
        for l in range(n):
            r = (l + k - 1) % n
            if q and q[-1] == colors[r]:
                q = deque()
            q.append(colors[r])
            # print(l, r, q, ans)
            if len(q) == k:
                ans += 1
                q.popleft()

        return ans
