class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> nums = colors;
        int size = nums.size();
        vector<int> counts(size, 1);

        for (int i = size - 2; i >= 0; --i) {
            if (nums[i + 1] != nums[i]) {
                counts[i] = 1 + counts[i + 1];
            }
        }

        for (int i = size - 1; i >= 0; --i) {
            if (nums[(i + 1) % size] != nums[i]) {
                counts[i] = 1 + counts[(i + 1) % size];
            }
        }

        int result = 0;
        for (int i = 0; i < size; ++i) {
            if (counts[i] >= k) {
                result += 1;
            }
        }

        return result;
    }
};
