class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++)
        {
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        int left=0,right=1;
        int count=0;
        while(right<n)
        {
            if(colors[right]==colors[right-1])
            {
                left=right;
            }
            if(right-left+1==k)
            {
                count++;
                left++;
            }
            right++;
        }
        return count;
    }
};
