class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        count = 0
        best = 0
        for a, b in pairwise(colors + colors[:k - 1]):
            if a != b:
                count += 1
                if count >= k - 1:
                    best += 1
            else:
                count = 0
        return best
