import java.util.*;

class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {
        int n = arr.length;
        int result = 0;
        int count = 1;
        int[] extendedArr = new int[2 * n];

        for (int i = 0; i < 2 * n; i++) {
            extendedArr[i] = arr[i % n];
        }

        for (int i = 1; i < n + k - 1; i++) {
            if (extendedArr[i] != extendedArr[i - 1]) {
                count++;
                if (count >= k) {
                    result++;
                }
            } else {
                count = 1;
            }
        }

        return result;
    }
}
