class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cols, int k) {
        int n = cols.size();
        vector<bool> alt(n,false);
        for (int i = 0; i < n; ++i) {
            if (!i) {
                alt[i] = (cols[0] != cols[1] && cols[0] != cols.back());
                continue;
            }
            if (i == n-1) {
                alt[i] = (cols[n-1] != cols[0] && cols[n-1] != cols[n-2]);
                continue;
            }
            alt[i] = (cols[i] != cols[i+1] && cols[i] != cols[i-1]);
        }
        int ct = 0, sol = 0;
        for (int i = 1; i < k-1; ++i) ct += alt[i];
        sol += (ct==k-2);
        for (int i = k; i < n; ++i) {
            ct -= alt[i-k+1];
            ct += alt[i-1];
            sol += (ct==k-2);
        }
        for (int i = 0; i < k-1; ++i) {
            int ind = (i-k+1+n)%n;
            ct -= alt[ind];
            ct += alt[(i-1+n)%n];
            sol += (ct==k-2);
        }
        return sol;
    }
};
