class Solution {
public:
int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size=1;
        int ans=0;
        int n=colors.size();
        for(int i=1; i<colors.size(); i++)
        {
        	if(colors[i]!=colors[i-1])
        	{
        		size++;
        	}
        	else
        	{
        		size=1;
        	}
        	if(size==k)
        	{
        		ans++;
        		size--;
        	}
        }
        if(colors[0]!=colors[n-1])
        {
        	size++;
        	if(size==k)
        	{
        		ans++;
        		size--;
        	}
        	for(int i=1; i<k-1; i++)
        	{
        		if(colors[i]!=colors[i-1])
        		{
        		size++;
        		}
        		else
        		{
        			size=1;
        		}
        		if(size==k)
        		{
        			ans++;
        			size--;
        		}
        	}
        }
        return ans;
    }
};
