class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int i=0,j=1;
        int ans=0;
        while(j<n+k-1){
            if(colors[j%n]!=colors[(j-1)%n]){
                if(j-i+1==k){
                    ans++;
                    i++;
                }
            }
            else{
                i=j;
            }
            j++;
        }
        return ans;
    }
};
