class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] arr = new int[n+k];
        for(int i=0;i<n;i++)arr[i] = colors[i];
        for(int i=n;i<n+k;i++)arr[i] = colors[i-n];
        n = arr.length;
        int cnt = 0;
        int[] pre = new int[n+1];
        for(int i=1;i<n;i++){
            pre[i] += pre[i-1] + Math.abs(arr[i]-arr[i-1]);
        }
        for(int i=k;i<n;i++){
            if(pre[i] - pre[i-k+1] == (k-1))cnt++;
        }
        return cnt;

    }
}
