class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ind = 0;
        int ju= 1;
        int kotoboro = colors.size();
        int fl = 0;

        while ((ju < (kotoboro + k)) && (ind < kotoboro)) {
            int shreyasi = ju;
            int tempEnd = shreyasi % kotoboro;
            while (colors[tempEnd] != colors[(tempEnd - 1 + kotoboro) % kotoboro] && shreyasi < kotoboro + k - 1) {
                shreyasi++;
                tempEnd = shreyasi % kotoboro;
            }
//             }

// void filled(vector<int> &bit , int n){
//     for(int i=0;i<31;i++){
//         int b = n&(1<<i);
//         if(b)bit[i]++;
//     }
// }

            fl += max(shreyasi - ind - k + 1, 0);
            ind = shreyasi;
            ju = ind + 1;
        }

        return fl;

    }
};
