class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        map<int,int>mp;
        int n=arr.size();
        for(int i=1;i<n;i++){
            if(arr[i]==arr[i-1])
            mp[i]++;
        }
        if(arr[n-1]==arr[0])
        mp[0]++;
        int prev = 0;
        int ans = 0;
        int temp = 0;
        int i=0;
        while(prev<n){
            int ineed=i%n;
            if(mp.find(ineed) != mp.end()){
            temp++;
            }
            if(i-prev+1==k){
                if(mp.find(prev) != mp.end()){
                temp--;
                }
                if(temp==0){
                ans++;
                }
                prev++;
            }
        i++;
        }
        return ans;

    }
};
