class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        left = 0
        n = len(colors)
        count = 0

        for right in range(n + k - 1):
            prev = colors[(right - 1) % n]
            cur = colors[right % n]

            if prev == cur:
                left = right

            if right - left + 1 >= k:
                count += 1


        return count
