class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {
        int n = arr.length, count = 0;
        if (k == 1) return n;
        int i = 1, altLen = 1;

        while (i<n+k-1) {
            int curr = i, prev = i-1;
            if (prev >= n) prev -= n;
            if (curr >= n) curr -= n;

            if (arr[curr] != arr[prev]) ++altLen;
            else altLen = 1;

            if (altLen >= k) ++count;
            ++i;
        }
        return count;
    }
}
