class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int left=0,n=colors.size(),ans=0,right=0;
        vector<int>modifiedColors(n,0);
        for(int i=0;i<n;i++)
        {
            modifiedColors[i]=colors[i]^colors[(i+1)%n];
        }
        int sum=0,newK=k-1;
        for(right=0;right<newK;right++)
        {
            sum+=modifiedColors[right];
        }
        if(sum==newK)
        {
            ans++;
        }
        for(int count=1;count<n;count++)
        {
            sum+=modifiedColors[right];
            sum-=modifiedColors[left];
            left=(left+1)%n;
            right=(right+1)%n;
            if(sum==newK)
            {
                ans++;
            }
        }
        return ans;
    }
};
