class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ans=0;
        int n=a.size();
        for(int i=0;i<n;i++) {
            a.push_back(a[i]);
        }
        int cnt=1;
        for(int i=1;i<n+k-1;i++) {
            if(a[i]!=a[i-1]) {
                cnt++;
            }
            else {
                if(cnt>=k) {
                    ans+=(cnt-k+1);
                }
                cnt=1;
            }
        }
        if(cnt>=k) {
            ans+=(cnt-k+1);
        }
        return ans;
    }
};
