class Solution {
    public int numberOfAlternatingGroups(int[] nums, int k) {
        int cnt=0;
        int n=nums.length,i=0,j=1;
        int c=1;
        while(j<k+n-1){
            if(nums[j%n]!=nums[(j-1+n)%n]){
                c++;
            }
            else{
                c=1;
            }
            if(c>=k){
                cnt++;
            }
            j++;
        }
        return cnt;
    }
}
