#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        int count = 0;
        int c = 0;

        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] != colors[i + 1]) {
                c++;
            }
        }

        if (c == k - 1) {
            count++;
        }

        for (int i = 1; i < n; ++i) {
            if (colors[i - 1] != colors[(i - 1 + 1) % n]) {
                c--;
            }

            if (colors[(i + k - 2) % n] != colors[(i + k - 1) % n]) {
                c++;
            }

            if (c == k - 1) {
                count++;
            }
        }

        return count;
    }
};
