class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        vector<int> temp;

        for(auto x: arr)
            temp.push_back(x);

        for(auto x: arr)
            temp.push_back(x);

        int s = temp.size();
        vector<int> dp(s,0);
        int res = 0;
        for(int i=s-1;i>=0;i--)
        {
            if(i==s-1){
                dp[i] = 1;
                continue;
            }

            if(temp[i] == temp[i+1]){
                dp[i] = 1;
            }else{
                dp[i] = dp[i+1]+1;
            }

            if(i<=n-1 && dp[i]>=k){
                res++;
            }
        }

        return res;

    }
};
