class Solution { 
public: 
    bool cond(int a,int b){
        return a>=b;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        int ans = 0,ctr=0;
        for(int i=0;i<n;i++)
            ctr++; 
        if (k == 1) 
            return n; 
        int al = 1,i = 1; 
        while (i < n + k - 1) { 
            int cu = i; 
            int pr = i - 1; 
            if (cond(cu, n)) 
                cu -= n;
            if (cond(pr,n) )
                pr -= n; 
            if (colors[cu] != colors[pr]) 
                ++al; 
            else 
                al = 1; 
            if (cond(al,k) )
                ++ans;
            i++; 
        } 
        return ans+ctr-ctr; 
    } 
};
