class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        vector<int>c(n+k-1);
        for(int i=0;i<n;i++){
            c[i] = colors[i];
        }
        for(int i=0;i<k-1;i++){
            c[n+i] = colors[i];
        }

        int n1 = c.size();
        int ans = 0,l = 1;
        for(int i=1;i<n1;i++){
            if(c[i] != c[i - 1]){
                l++;
            }
            else{
                l = 1;
            }
            if(l>= k){
               ans++;
            }
        }

        return ans;
    }
};
