class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0;i < k - 1;i++)
        colors.push_back(colors[i]);
        vector<int> v;
        int s = 0,e = 0;
        int ans = 0,l = 1;
        for(int i = 0;i < colors.size() - 1;i++){
           if(colors[i] != colors[i + 1]){
            l++;
           }
           else if(l >= k)
           {
                v.push_back(l);
                l = 1;
           }
           else 
           l = 1;
        }
        if(l >= k)
        v.push_back(l);
        for(auto &i:v)
        {
            ans += i - k + 1;
        }
        return ans;
    }
};
