class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       int purnank = 0; 
        int lambai = 1; 
        int shuru = 1; 
        while (shuru < colors.size() + k - 1) { 
            int pichlaind = shuru - 1;
            int yeind = shuru; 

           pichlaind>=colors.size()?pichlaind-=colors.size():pichlaind-=0;
            yeind>=colors.size()?yeind-=colors.size():yeind-=0;


            if (colors[yeind] != colors[pichlaind]) { 
                lambai+=1; 
            } 
            else { 
                lambai = 1; 
            } 

            if (lambai >= k) { 
                purnank+=1; 
            } 

            shuru++; 
        } 

        return purnank; 
    } 
};
