class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)-1
        for i in range(k-1):
            colors.append(colors[i])

        check = [0]*(len(colors)-1)
        for i in range(len(colors)-1):
            if colors[i]+colors[i+1] == 1:
                check[i] = 1


        val = sum(check[:(k-1)])
        if val == k-1:
            ans = 1
        else:
            ans = 0

        for i in range(n):
            val += check[k+i-1]-check[i]
            if val == k-1:
                ans += 1

        return ans
