class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v=colors;
        for(auto it:colors)v.push_back(it);
        int n=v.size();
        vector<int>prefix(n+1,0);
        for(int i=0;i<n;++i)
        {
            if(i-1>=0)
            {
                if(v[i]==v[i-1])
                {
                    prefix[i]++;
                }
            }
            if(i>0)prefix[i]+=prefix[i-1];
            // cout<<prefix[i]<<" ";
        }
        int ans=0;
        int done=0;
        for(int i=0;i<colors.size();++i)
        {
            int l=prefix[i];
            int r=prefix[i+k-1];
            if(r-l==0)++ans;
        }
        return ans;
    }
};
