class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<n;i++) {
            colors.push_back(colors[i]);
        }

        int ans = 0;
        vector<int> res;
        for(int i=0;i<colors.size()-1;i++) {
            if(colors[i]!=colors[i+1]){
                res.push_back(1);
            }else{
                res.push_back(0);
            }
        }
        // for(int i=0;i<res.size();i++) cout<<res[i]<<" ";
        int num = 0;
        k--;
        for(int i=0;i<k;i++) {
            if(res[i]==1) num++;
        }
        if(num==k) ans++;
        cout<<num<<" "<<ans<<endl;
        for(int i=k;i<res.size();i++) {
            if(i-k+1>=n) break;
            if(res[i]==1) num++;
            if(res[i-k]==1) num--;
            if(num == k) ans++;
        }
        return ans;
    }
};
