class Solution {
    public int numberOfAlternatingGroups(int[] varna, int yuga) {
            int prithvi = varna.length;
        int[] vistaritaVarna = new int[prithvi + yuga];
        System.arraycopy(varna, 0, vistaritaVarna, 0, prithvi);
        System.arraycopy(varna, 0, vistaritaVarna, prithvi, yuga);

        prithvi = vistaritaVarna.length;
        int ganana = 0;
        int[] purvaBheda = new int[prithvi + 1];

        for (int i = 1; i < prithvi; ++i) {
            purvaBheda[i] = purvaBheda[i - 1] + Math.abs(vistaritaVarna[i] - vistaritaVarna[i - 1]);
        }

        yuga--;

        for (int i = yuga + 1; i < prithvi; ++i) {
            if (purvaBheda[i] - purvaBheda[i - yuga] == yuga) {
                ganana++;
            }
        }

        return ganana;
    }
}
