class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size();

        for(int i=0;i<k-1;i++){
            nums.push_back(nums[i]);

        }

        vector<int> vec;
        vec.push_back(1);


        for(int i=1;i<nums.size();i++){
            if(nums[i]!=nums[i-1]){
                vec.push_back(vec[i-1]+1);
            }
            else{
                vec.push_back(1);
            }
        }



        int ans =0;
        for(int i=0;i<n;i++){
            if(vec[i+k-1]-vec[i]==k-1)ans++;

        }



        return ans;
    }
};
