class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 

        for (int i = 0; i < k; i++) colors.push_back(colors[i]);
        int n = colors.size(), cnt = 0; 
        k -= 1;

        map < int, int > mp;
        mp[0] = 0;

        for (int i = 1; i < n; i++) {

            mp[i] += mp[i - 1] + abs(colors[i] - colors[i - 1]); 
            if (i > k) cnt += (mp[i] - mp[i - k] == k);

        }

        return cnt; 

    } 
};
