class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size(),ans=0;
        vector<int> altern(n,1);

        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                altern[i]=altern[i-1]+1;
            }
        }

        for(int i=0;i<n;i++){
            if(i==0 && colors[i]!=colors[n-1]){
                altern[i]=altern[n-1]+1;  
            }
            else if ( i!=0 && colors[i]!=colors[i-1]){
                altern[i]=altern[i-1]+1;
            }

            if(altern[i]>=k)ans++;
        }

        return ans;


    }
};
