class Solution {
public:

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>arr;
        int n=colors.size();
    for(int i=0;i<n;i++){
    arr.push_back(colors[i]);
    }
        for(int i=0;i<k-1;i++){
            arr.push_back(colors[i]);
        }
        int ans=0;
        int xx=1;
        for(int i=1;i<arr.size();i++){
            if(arr[i]!=arr[i-1]){
                xx++;
            }
            else {
                xx=1;
            }
            if(xx>=k){
                ans++;
            }
        }

        return ans;
    }
};
