class Solution {
    public int numberOfAlternatingGroups(int[] C, int k) {
        int n = C.length;
        int R = 0;
        int N = 1;
        int z=0;
        int[] extendedC = new int[n + k - 1];
        System.arraycopy(C, 0, extendedC, 0, n);
        System.arraycopy(C, 0, extendedC, n, k - 1);

        for (int i = 1; i < extendedC.length; i++) {
            if (extendedC[i] != extendedC[i - 1]) {
                N++;
            } else {
                N = 1;
                z++;
            }
            if (N >= k) {
                R++;
                z--;
            }
        }

        return R;

    }
}
