class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        vector<int>v = c;
        v.insert(v.end(), c.begin(), c.end()); 
        int cnt = 0;
        int j = 0, i = 1;
        while (j < n) 
        {
            if (v[i]==v[i - 1]) 
            {
                j = i;
                i++;
                continue;
            }
            if (i - j + 1 < k) 
            {
                i++;
                continue;
            } 
            else 
            {
                cout<<i << " "<<j <<" "<<cnt;
                i++;
                j++;
                cnt++;
            }
        }
        return cnt;
    }
};
